import React, { useEffect, useState } from 'react'
import axios from 'axios'
import DataTable from "react-data-table-component"; 
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.min.js';

const ContruiesTable = () =>{
    const [countries,setCountries]=useState([]);
    const getCountries=async()=>{
      try{
        const response=await axios.get('https://restcountries.com/v2/all');
        setCountries(response.data);
      }catch(error){
        console.log(error);
      }
    };
  
    useEffect(()=>{
      getCountries();
    },[]);
    
  
    const columns=[
  
      {
        name:"Country Name",
        selector:(row)=>row.name
      },
      {
        name:"Country Native Name",
        selector:(row)=>row.nativeName
      },
      {
        name:"Country Capital",
        selector:(row)=>row.capital
      },
      {
        name:"Country flag",
        selector:(row)=><img width={50} height={50} src={row.flag} alt={row.name}/>
      },
      {
        name:'Action',
        cell:   (row)=><button className='btn btn-primary' 
        onClick={()=>alert(row.callingCodes)}
        > Edit</button>
      }
      
  
    ]
    return <DataTable
     title="Country List" 
     columns={columns} 
     data={countries}
      pagination
      fixedHeader
      fixedHeaderScrollHeight='400'
      selectableRows
      selectableRowsHighlight
      highlightOnHover
      />
}
export default ContruiesTable;
